CREATE TABLE `information-market` (
  `id` smallint(6) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '名字',
  `price` varchar(40) COLLATE utf8_unicode_ci NOT NULL COMMENT '价格',
  `market` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '市场名字',
  `time` varchar(80) COLLATE utf8_unicode_ci NOT NULL COMMENT '时间'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='市场行情动态';

