--
-- 表的结构 `nongzi-huafei-fuhe`
--

CREATE TABLE `nongzi-huafei-fuhe` (
  `id` smallint(6) NOT NULL,
  `view` int(11) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `canshu` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品参数',
  `chanping` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品说明',
  `picture` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '图片'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 转存表中的数据 `nongzi-huafei-fuhe`
--

INSERT INTO `nongzi-huafei-fuhe` (`id`, `view`, `name`, `canshu`, `chanping`, `picture`) VALUES
(1, 1, '钙镁复合肥', '玉彤|纯白色颗粒/酸碱度为中性/百分之百溶于水|面议|1 吨|山西省  太原市  万柏林区 万柏林街.|长期有效', '硝酸钙镁为纯白色颗粒，酸碱度为中性，且百分之百溶于水，是一种全硝态氮型作物补钙补镁型产品。本品中的硝态氮无需在土壤中转化而直接为作物吸收，加快作物光合作用。本品施入土壤不会引起板结，其中的钙离子可以调节土壤酸碱度并促进作物对土壤中氮、磷、钾的吸收量增加，增加作物抵抗力，能有效预防作物因缺钙引起的西红柿倒伏、脐腐病，黄瓜的生长点坏死，白菜干烧心、空心开裂、软化病，苹果的苦痘病、梨的黑斑病、褐斑病等众多生理性病害，作物施用本品可使细胞壁增厚，增加叶绿素含量及促进糖水化合物的形成，使水果蔬菜的储运期延长、粮食作物籽粒饱满、千粒重增加。同时可使果品贮藏期间增加果实硬度，明显增加果实外观色泽与光洁度，改善品质、提高产量，提升果品等级。同时，本品加入微量元素硼，可帮助植物更好的吸收钙、镁。|水果含糖量低的主要原因与作物缺镁有直接联系，而土壤中的活性铝导致作物不能正常吸收镁及别的养分，本品施入土壤侯可使土壤中活性铝浓度降低，本品硝态氮于镁的独特配方，不仅能增加作物对镁的吸收量，而且促进作物对磷的吸收，同时增加水果糖分及作物淀粉含量。硝酸钙镁：其中含氮13，含氧化钙15，氧化镁6。', 'picture/huafei/fuhe/0.jpg'),
(2, 1, '新型肥料', '中科肥料|总氮≥23.5%/总氮(N)≥23.5%/总氮(N)≥23.5%|面议|无|湖南省 永州市 东安县 芦洪市镇|2018-03-21', '改善土壤透气、保湿、疏松、有利于作物根系发育，茎秆粗壮，抗倒伏能力强，产量高，品质好。促进秧苗迅速扎根，根系发达，成活率高。缩短缓苗期，水稻返青快、叶片宽厚浓绿，分蘖多、孕穗早、穗大穗多、籽粒饱满。施用本品后不用施用其它化学除草剂，真正绿色环保。同比增产15%以上，显著提高稻米品质。|该产品以植物源功能性有机营养颗粒为原料，添加了一定比例的氮、磷、钾和腐殖酸精制而成。使用该产品能调节农作物的内循环，提高农作物对肥料的吸收利用率。改善土壤透气、保湿、疏松、有利于作物根系发育，茎秆粗壮，抗倒伏能力强，产量高，品质好。缩短缓苗期，水稻返青快、叶片宽厚浓绿。分蘖多、孕穗早、穗大穗多、籽粒饱满。有效预防水稻因缺乏微量元素的蹲苗、僵苗、扁粒、炸壳等现象。改良土壤，恢复地力，同比增产10%-15%，显著提高稻米品质\r\n', 'picture/huafei/fuhe/2.png'),
(3, 1, '进口复合肥', '进口复合肥-采法特复合肥以色列进口复合肥|无|900.00元/箱|10 箱|北京市 市辖区 朝阳区|长期有效', '复合肥采法特复合肥料由以色列国家功能肥实验室研制，是全球最早以三磷酸腺苷为核心物质的功能流体肥料，同时加入18种氨基酸及大中微量元素、稀土元素、阳光杆菌、中微量元素，经过电解、磁化等工艺，为作物提供充足能量和营养，大幅度提高产量，不同作物增产30%——60%以上 ，被世界农业专家誉为“植物增产动力。本品由以色列夫沃施功能肥国家实验室研发，利用分子化合技术生产，不含激素，氯离子，技术先进，全水溶，作物更容易吸收。|\r\n性能特点:|\r\n1、分子化合技术能使营养物质更小、更均匀，更利于作物吸收，利用率大大提升。|\r\n2、复合肥采法特添加进口螯合微量元素，各养分之间相互促进吸收，不与其它营养元素发生拮抗作用。|\r\n3、高品质氮、磷、钾均衡供应利于根系吸收，见效快，肥效长，增产显著。|\r\n4、不含激素、氯离子，硫酸根离子、对作物安全，长期使用不会造成土壤盐化、酸化、板结，适合任何作物。|\r\n5、 复合肥采法特复合肥料利用营养均衡释放技术，养分能够均匀的被作物利用，提高利用率。|\r\n特殊功效：|\r\n复合肥采法特复合肥料内含三磷酸腺苷，提高作物细胞抗性，强化作物自身机能，为作物生长提供充足的能量，对寒、旱、涝、雹等自然灾害有很好的改善、修复作用。改善作物品质、提前上市3-5天。', 'picture/huafei/fuhe/3.jpg'),
(4, 1, '复合肥料-翠姆复合肥料以色列真正进口肥', '以色列拉姆拉翠姆|1千克/10千克|面议|10 件|山东省 烟台市 芝罘区 芝罘岛街|长期有效', '本品由以色列拉姆拉特种肥料有限公司研发，氮磷钾科学配比，添加微聚富里酸和中微量元素，全水溶，营养全面。并能有效改良土壤，减轻盐碱现象。|\r\n1.不同于市面上的掺混技术产品，不容易结块，有效利用率大大提高。|\r\n2.本品为特种肥料，能有效提高中微量元素如钙、镁、铁、锌、硼等在土壤下的有效性。|\r\n3.安全性高，不含氯离子、硫酸根离子和激素，不会造成土壤盐渍化。|\r\n4.有效预防作物早衰，减少作物缺素症状，预防小叶病，黄叶病等。|\r\n5.极大的促进作物开花率、座果率，生根好，碰过速度快，增产显著。|\r\n复合肥料可分为：|\r\n平衡型：20:20:20|\r\n高钾型：12:7:40|\r\n高氮型：30:10:10|\r\n高磷型：10:50:10', 'picture/huafei/fuhe/4.jpg'),
(5, 1, '供应澳仕达牌高含量【流体复合肥】---高磷高钾型', '澳仕达牌|200ml(330g)x40瓶/箱|1000.00元/箱|1 箱|陕西省 咸阳市 杨陵区|长期有效', '流体复合肥简介：液体复合肥又称流体复合肥，俗称液肥，液体复合肥可以根据作物生长所需要的营养需求特点来设计肥料配方，随时根据作物不同长势对肥料配方作出调整。科学的配方可以显著地提高肥料利用率，液体肥料的肥效通常比常规复合肥料高30%以上。|澳仕达牌高含量流体复合肥研制背景：液体复合肥是一种新型肥料。国际肥料消费正在向高浓度、复合化、液体化、缓效化的方向发展。液体复合肥由于具有生产养分含量高、易于复合、能直接被农作物吸收、便于配方施肥、滴灌喷灌施肥和机械化施肥等诸多优点，是肥料业发展的必然趋势越来越受到各国的普遍关注。世界上发达国家的农业集约化和产业化水平高，为农业机械化耕作和机械化施肥创造了良好的条件，因此液体复合肥在这些国家得到了广泛应用。随着全球经济一体化进程的加快，发展中国家为了促进本国的农业发展，降低农业生产成本，高档农产品在国际市场上的竞争力，开始采取一系列措施调整农业结构，提高农业集约化和产业化水平。机械化施肥条件逐步得到改善，对专用肥料的需求量不断增加，进而为液体复合肥的发展提供了比较广阔的空间。|澳仕达牌高含量流体复合肥产品简介：澳仕达牌复合流体肥是我公司技术人员和西北农林科技大学土肥专家及以色列农业专家历经多年突破了多个发达国家一直封锁的技术壁垒研制的一种具有国际先进水平的高含量绿色环保新型流体复合肥，本品全部采用进口高含量原料，并投巨资引进国外系统化生产设备：催化氧化系统、初虑设备、发酵装置、螯合设备、精虑设备、稳定装置，生产的全球含量最高的流体复合肥，该产品含量高达84%，是目前为止世界上含量最高的流体复合肥，该产品的研制成功标志着我公司在该技术领域已到达并超越国际先进水平。', 'picture/huafei/fuhe/5.jpg'),
(6, 1, '60%复合氨基酸粉', '南通普悦生物医药|未知|面议|无|江苏省 南通市 崇川区|长期有效', '复合氨基酸粉 氨基酸含量60%以上', 'picture/huafei/fuhe/6.jpg'),
(7, 1, '供应进口淘金者-豆角专用冲施肥', '淘金者|未知|面议|无|广东省 深圳市 宝安区 光明|长期有效', '产品特点：|\r\n本品是绿普信专家采用一流的航天生物技术研制的最新一代超浓缩绿色无公害豆角专用冲施母液，富含有豆角所需的各种大、中、微量元素、符合生物菌和作物生长所必须的激活因子，能迅速被吸收，使其根系变粗，同时可促进花芽分化，提高豆角座果率，保花保果，使豆角拉长拉粗，口感好、纤维量含量低，增产30%-60%。且提前上市，能显著提高豆角植株抗寒、抗旱、抗涝、抗盐碱、抗重茬、抗病能力，并能疏松土壤，提高地温，改良土壤，降解农药残留，延长采收期20-30天|\r\n产品性能：|\r\n1.提高光合作用促进根系发达，提高抗病能力使豆角叶片肥大、茎杆粗大、节间缩短、增加开花数量，促进授粉，坐果率高，保花、 保果、保荚、结荚多，增强二次结荚能力，加速细胞分裂、快速拉长豆角长度。|\r\n2.增强各种豆角膨大及身长宽度，可有效预防豆角炭疽、烧叶、灰霉、锈病以及黄叶、小叶、落花霉荚、豆小条细等多种不良症状。|\r\n3.抗冻害、抗旱，抗涝、抗逆、抗重茬，控制狂长使豆角膨大快、采收期长、籽粒饱满，绿嫩青脆，提早上市7-10天，增产增收。|\r\n适用作物及使用方法：|\r\n1.本品适用于扁豆、四季豆、荷兰豆、油豆、刀豆、豌豆、豇豆等豆类作物。|\r\n2.本品原液一壶加水10-15公斤稀释后可用于1.5-2亩地，滴灌每亩使用原液1-2公斤。|\r\n3.大棚豆角使用，10-20天冲施一次。|\r\n4.露地豆角15天冲施一次。|\r\n5.每亩每次用原液2.5-3公斤。|\r\n6.该产品随水冲施，省时省力，可以活化土壤，提高肥效，加速生物酶的活力，见效快，肥效长，2小时可被作物吸收，2天后叶面肥厚，植株健壮，豆角采摘期使用效果更佳。\r\n', 'picture/huafei/fuhe/7.jpg'),
(8, 1, '卢大哥牌大量元素复合肥料18-10-30+TE', '卢大哥牌|10kg/袋|8.00元/千克|1000 千克|山东省 济宁市 市中区 南苑街|长期有效', '营养速吸收易水溶卢大哥牌大量元素复合肥料18-10-30+TE|\r\n复合肥料 复合肥 肥料 水溶肥 花肥 蔬果肥 蔬菜肥料|\r\n产品特点：养全面、快速吸收、科学的N、P、K配比，整合态微量元素快速吸收，能充分满足各种蔬菜、果树和经济作物生长中后期对钾元素和微量元素的需求。|\r\n产品优势：水溶性好：配方中的原料采用全水溶的大量元素、整合态级别的微量元素，能满足滴灌和冲施的要求，吸收和利用率大幅度提升。\r\n肥效显著：特别添加的肥料高效剂能显著提高N、P、K在植物体内的传导，具有固氮和提高转化率的特点。提高作物吸收能力。省肥20%-30%。|\r\n土壤修复：添加了进口助剂，养分易吸收，融入本品后，同时能够疏松土壤，打破土壤板结，通过土壤团粒结构的修复，有效调节土壤中水、肥、气、热的平衡。显著促进根系生长，改善土壤中有益微生物生长环境，提高微生物生物活性，减少根部病害的发生和蔓延，促进土壤环境改善。|\r\n提高品质：使用本品后，能明显减少生理落果。畸形果和裂果的发生，光洁果面，增加水溶性糖分和维生素等营养物质的含量。\r\n生物复合肥料 有机复合肥 膨果肥 土壤肥\r\n ', 'picture/huafei/fuhe/8.jpg');

