--
-- 表的结构 `nongzi-huafei-jia`
--

CREATE TABLE `nongzi-huafei-jia` (
  `id` smallint(6) NOT NULL,
  `view` int(11) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `canshu` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品参数',
  `chanping` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品说明',
  `picture` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '图片'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 转存表中的数据 `nongzi-huafei-jia`
--

INSERT INTO `nongzi-huafei-jia` (`id`, `view`, `name`, `canshu`, `chanping`, `picture`) VALUES
(1, 1, '山东绿禾肥业高氮高钾高钙持续高产型水溶肥', '恒众|10公斤桶装粉剂（浅粉色）|180.00元/桶|10 桶|山东省 淄博市 淄川区 松龄路街|长期有效', '【通用名】：大量元素水溶肥料|\r\n【登记】：农肥（2013）临字7601号|\r\n【执行标准】：NY107-2010|\r\n【总含量】  50%|\r\n【成分含量】：总氮≥14%(含硝态氮) 总磷≥8% 总钾≥28% 氧化钙6%|\r\n【适用作物】：所有经济作物及果树。|\r\n【功效】： 全营养高氮高钾加钙添加中微量元素|\r\n【特点】：添加LLD-FHS技术，使磷钙共存。|\r\n【使用方法】： 稀释800-1000倍叶面喷施。滴灌冲施每亩每次5-10公斤。|\r\n【存放】：  阴凉干燥处，儿童不可触及之处，微结块不影响使用|\r\n【保质期】：3年', 'picture/huafei/jia/1.jpg'),
(2, 1, '大量元素水溶肥7-8-45高钾肥幼果到膨大期专用钾肥', '龙灯巨力星|1kg 5kg|1.00元/吨|1 吨|河南省 开封市 禹王台区|长期有效|', '产品特点：|\r\n本品由加拿大多伦多大学农学博士依据多地实验研发，为实现绿色无公害农业，农学博士采用有机与无机结合，突破常规，采用新工艺更利于作物吸收生长，根据本地作物生长特点，攻克一系列难题，植物吸收后，相互发挥优势，能迅速作用于作物细胞末端，激活植物导管，产生免疫抗体，启动作物生长系统，就像钛裂变瞬间爆发千倍能量，几分钟多点放射浸入植物体内，能使作物快速平衡生长，补充所需营养，将尖端生化技术应用到绿色农业领域。|\r\n产品功效：|\r\n一、促生激活：激活植物细胞生长系统，促使作物纵横生长，加快细胞分裂，补充养份，刺激作物生长，打破休眠，促使植物快速发芽，补充营养，促进花芽分化,强壮株体，芽壮苗齐。|\r\n二、促根壮苗：依据植物生长原理，解决根部难吸收障碍，调理生长均衡，强壮株体，促根、壮根、护根、养根，根多须壮，根壮芽齐， 激发新根，改善作物生长环境，减少生理性生长缓慢障碍。|\r\n三、保果膨大：促进花粉管伸长拉开，提高座果率，防止落花落果，减少裂果，畸形果的发生；增加果柄的韧性，膨大果实，增甜着色，果脆汁多。|\r\n四、解毒防衰：有效缓解作物药害肥害，清除毒素，疏松土壤，缓解板结，激活植物末端细胞，延缓植物老化，防止作物早衰。|\r\n五、生物抗重；微生物低温螯合提纯，促使作物快速吸收，增加内源酶的活性，补充植物所需养份，增强光合作用，改善土壤环境，自动切换生长系统，提高防旱、防涝、抗盐碱能力，解决因重茬耕作引发的生长缓慢障碍。|\r\n六、抗病保肥：自动打开免疫系统，抑制病菌侵染，锁肥固氮保水，减少肥力流失，刺激作物生长，打破休眠，促使植物快速发芽，补充养分。|\r\n适用作物：|\r\n草莓、葡萄、香梨、西红柿、芒果、大枣、黄瓜、甘蔗、豆角、花卉、中药材。|\r\n适用方法：|\r\n冲施：每660平方用本品5公斤随水冲入沟内，覆土即可。|\r\n滴灌：每1公斤兑水600-800公斤搅匀随管滴入。|\r\n机施：每5公斤用于660平方施于土壤随水浇匀。', 'picture/huafei/jia/2.jpg'),
(3, 1, '硝基水溶性钾肥', '玉彤|未知|面议|1 吨|山西省 太原市 万柏林区 万柏林街|长期有效', '性质：又称硝石。无色斜方结晶或白色结晶粉末。密度2.109g/cm3。熔点334℃。易溶于水。在400℃时分解放出氧，并转变成亚硝酸钾。为强氧化剂。由硝酸钠加适量水加热溶解，加入氯化钾进行复分解反应，经蒸发浓缩，先析出氯化钠结晶，过滤，滤液经冷却结晶，过滤，水洗，固液分离，干燥制得。有低温萃取法及高温法。还可用作玻璃澄清剂，农作物和花卉的复合肥料和选矿剂。|\r\n用途：机械热淬的盐浴；陶瓷工业用于制造 彩釉；用作玻璃澄清剂，制造汽车灯玻璃、光学玻璃和显像管玻壳等。生产青霉素钾盐、利福平等医药。还用作选矿药剂。食用硝酸钾用作肉制品的发色剂、防腐剂。农业用作肥料。\r\n包装：外套编织袋、复合纸袋。内衬塑料袋，净重50公斤', 'picture/huafei/jia/3.jpg'),
(4, 1, '硝酸钾钙', '玉彤|未知|面议|1 吨|山西省 太原市 万柏林区 万柏林街|长期有效', '功能特点|\r\n本品为全营养水溶肥，可广泛适用于：黄瓜、西瓜等瓜类，西红柿、辣椒等茄果类经济作物。|\r\n推荐理由|\r\n1.采用国际先进生产工艺，高钾高钙养分配比科学|\r\n2.特别添加高渗透因子，膨果增甜迅速，增产效果更明显|\r\n3.补钙、促根快、提高作物抗病能力,预防作物生理性病害|\r\n使用方法及登记作物|\r\n1.苗期：1-2kg一亩地(667平方)浇根或随水冲施|\r\n2.膨果初期：4-6kg一亩地（667平方）滴灌或随水冲施|\r\n3.膨果高峰期：8-10kg一亩地（667平方）滴灌或随水冲施|\r\n注：生长期间10-15天施用本品一次，膨果关键期可适当增加使用量及使用次数|\r\n本品为不规则造粒，采用先进技术合成100%溶于水，是一种新型、高效钙镁复合肥料，酸碱度属中性。所含钙、镁离子可增加作物光合作用，提高碳水化合物得含量，修复叶片阶段性失绿，促进作物对土壤中氮、磷、钾吸收。其中大、中、微量元素协调互助，使营养全面，合理配比提高利用率同时节约了成本，系改善果实内、外在品质的新一代全功能绿色环保肥料。|\r\n产品特点|\r\n 1、适用于果树、蔬菜的任何时期，施用后可快速提苗壮秧，庞大根系，提高坐穗、坐果率、提高品质增加产量。|\r\n2、  能效预防作物因缺钙引起的西红柿脐腐病，黄瓜的生长点坏死，白菜干烧心，空心开裂、软化病，苹果的苦痘病，梨的黑斑病、褐斑病等众多生理性病害', 'picture/huafei/jia/4.jpg'),
(5, 1, '硝铵磷', '玉彤|未知|面议|1950 吨|山西省 太原市 万柏林区 万柏林街|长期有效', '氮大于32%，磷大于4%，水分小于1%，白色，颗粒（2-4mm)/30-6/30-4|\r\n硝铵磷肥：白色圆形造粒，无臭，味咸，略苦。|\r\n颜色：白色圆形造粒|\r\n有效成分：总氮含量32 总养分含量〉：36%\r\n总氮含量〉：4% 有效磷含量〉4%|\r\n颗粒：90% 用量：40-60（kg/亩）|\r\n性质：白色圆形造粒，无臭，味咸，略苦。易潮解。微溶于甘油和丙|酮。与有机物，硫磺。\r\n用途：适用于多种土壤和作物，广泛应用与温室和花，果树，蔬菜，烟草等。工业上应用于金属表面清洁物。', 'picture/huafei/jia/5.jpg'),
(6, 1, '氢氧化钾', '成都盛大创业化工有限公司|未知|1.00元/件|1 件|四川省 成都市|长期有效', '成都盛大创业化工有限公司主营化工产品（硫磺、硫酸、盐酸、液碱、氯化钾、尿素、硝酸钾、氢氧化、钾硫酸钾、磷酸等），其中以氯化钾、硫磺贸易为主营业务，多年来公司坚持“一业为主，多种经营”的方针，先后成功进入了化工、仓储、物流行业，为我司硫磺客户形成一条龙服务打下了良好基础。现已同国内外同行业、央企、国有、民营大公司建立了良好的业务合作关系。展望未来，公司将抓住西部大开发的历史机遇，加快企业发展步伐，为四川经济添砖加瓦，再创辉煌。氢氧化钾用作干燥剂、吸收剂，用于制草酸及各种钾盐，还用于电镀、雕刻、石印术等。主要用作钾盐生产的原料，如高锰酸钾、碳酸钾等。在医药工业中，用于生产钾硼氢、安体舒通、沙肝醇、丙酸睾丸素等。在轻工业中用于生产钾肥皂、碱性蓄电池、化妆品（如冷霜、雪花膏和洗发膏）。在染料工业中，用于生产还原染料，如还原蓝RSN等。在电化学工业中，用于电镀、雕刻等。在纺织工业中，用于印染、漂白和丝光，并大量用作制造人造纤维、聚酯纤维的主要原料。此外，还用于冶金加热剂和皮革脱脂等方面。化工基本原料，用于医药、日用化工等。用作分析试剂、皂化试剂、二氧化碳和水分的吸收剂。日化工业用作制造洗污肥皂、洗头软皂、雪花膏、冷霜、洗发膏等的原料。制药工业用于制造黄体酮、香兰素等原料。染料工业用于制造三聚氰胺染料。电池工业用于制造碱性蓄电池。 选成都盛大创业化工有限公司。 感谢关注我们公司，如需购买专业生产氢氧化钾,成都专业生产氢氧化钾,成都进口氢氧化钾,便宜的氢氧化钾,成都便宜的氢氧化钾,专业制造氢氧化钾,成都专业制造氢氧化钾,氢氧化钾批发,氢氧化钾直销,氢氧化钾直销公司等更多问题请来电咨询，成都盛大创业化工有限公司期待您的光临！', 'picture/huafei/jia/6.jpg'),
(7, 1, '龙灯巨力星磷酸二氢钾', '龙灯巨力星|400g 25kg|1.00元/件|1 件|河南省 开封市 禹王台区|长期有效', '磷酸二氢钾作为生理性中性肥料，具有良好的水溶性，因其含有52%的磷（P2O5）和34%的钾（K2O）,溶解率在99.9%以上，作为叶面钾肥的首选，同时可以作为根施追肥。|\r\n磷酸二氢钾同时包含磷钾两大元素。磷可以促进花芽分化，提早开花结果，促进幼苗根系生长和改善果实品质。缺磷时，幼芽和根系生长缓慢，植株矮小，夜色暗绿，无光泽，背面紫色。磷肥一般作基肥，也用磷酸二氢钾作叶面喷施，或者进行根外追肥。|钾肥能促进植株茎杆健壮，改善果实品质，增强植株抗寒能力，提高果实的糖分和维生素C的含量，缺钾症状首先老叶遭殃，老叶变黄，容易早衰。钾元素供应不足，碳水化合代谢收到干扰，光合作用受到抑制，呼吸作用加强。因此，缺钾时植株抗逆能力减弱，易受病害侵袭，果实下降，着色不良。尤其是钾肥在幼果至膨大期，钾肥的需求量增大。磷酸二氢钾在花木移栽时，蘸根栽植，促进根系生长，使其枝壮叶茂，可以迅速被吸收，抗病性、抗逆性增强。很多花卉在花芽分化时期用磷酸二氢钾，进行叶面喷雾，可以促进花芽分化，增甜花卉的花朵量，促使其叶宽、花香。', 'picture/huafei/jia/7.jpg'),
(8, 1, ' 进口水溶肥 叶面肥', '果顿|25Kg一袋|600.00元/袋|30 袋|山东省 青岛市 城阳区 流亭街|2017-12-29', '产品名称： 英国原包进口果顿大量元素水溶肥|\r\n含量配比：19-19-19+TE|\r\n规     格： 25Kg/袋|\r\n适用作物：所有经济作物|\r\n颜色：深绿色|\r\n产品形态：深绿色水溶性晶体|\r\n产品特点：|\r\n营养均衡：氮磷钾及各种微量元素按照作物需求科学配比，不同配方满足不同发育阶段的养分需求。长期使用不板结土壤:只含高品质养分，不含钠、氯等有害离子。|\r\n完全水溶：溶解迅速。施用方便，可以冲施、喷施、穴施、通过滴灌系统施用，施用后迅速补充作物养分。|\r\n金属微量元素100%鳌合态：含量高，易被植物吸收利用。|\r\n适宜的PH值:弱酸性适宜于作物的生长需要。\r\n良好的兼容性：可与绝大多数农药化肥混用，建议喷施前做安全试验。\r\n', 'picture/huafei/jia/8.jpg');

