--
-- 表的结构 `nongzi-zhongzi-datian`
--

CREATE TABLE `nongzi-zhongzi-datian` (
  `id` smallint(6) NOT NULL,
  `view` int(11) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `canshu` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品参数',
  `chanping` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品说明',
  `picture` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '图片'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 转存表中的数据 `nongzi-zhongzi-datian`
--

INSERT INTO `nongzi-zhongzi-datian` (`id`, `view`, `name`, `canshu`, `chanping`, `picture`) VALUES
(1, 1, '德油5号油菜种子 适合9月下旬播种', '南箭|未知|面议|无|湖北省 武汉市 武昌区 中华路街|长期有效', '产品名称： 德油5号油菜种子  适合9月下旬播种|\r\n\r\n优势： 精选 杂交种 |\r\n \r\n包装： 80G/塑料袋| \r\n\r\n属性： 纯度不低于85% 净度不低于98% 芽率不低于85% 水分不高于9% |\r\n\r\n特性：\r\n  属甘蓝型中熟油菜杂交新品种，全生育期223天i分枝9-11个，二次分枝5个，为匀生分枝型，单株角果熟410.8个，千粒重3.87克，含油率39.49%，中抗病毒，菌核病。 |\r\n\r\n栽培方法：  \r\n  1999年四川省油菜区试中比对照中油821增产35.16%，2000年四川省生产试验中比对照蓉油4号增产9.83%。| \r\n中文名:油5号油菜|\r\n 特点:茎，叶宽大，绿色 栽培要点9月下旬播等 |\r\n 品种  ： 甘蓝型中熟两系杂交种 \r\n 特征特性该品种属甘蓝型中熟两系杂交种，株高200厘米左右，绿茎，叶宽大，绿色，叶片缘皱折明显，有锯齿，叶茎多蜡粉。一次分枝9~11个，二次分枝5个，为匀生分枝型，单株角果数410.8个，角果粒数14.3粒，千粒重3.87克，含油率39.49%。|\r\n栽培要点育苗移栽9月下旬播种，直播10月上旬播种。亩植密度6500~8000株，直播1.2万~1.5万株。亩施纯N8约11公斤，磷肥40~50公斤，钾肥10公斤，硼肥1公斤，重底早追。[1]    \r\n备注：价格为公斤价，50公斤起为20.9元/公斤，100公斤以上17.9元/公斤 含运费\r\n', 'picture/zhongzi/datian/1.jpg'),
(2, 1, '澳洲80天油菜心种子', '南箭| 25G/塑料袋 |面议|无|湖北省 武汉市 武昌区 中华路街|长期有效', '产品名称： 澳洲80天油菜心种子| \r\n\r\n优势： 粗条，嫩脆 |\r\n\r\n包装： 25G/塑料袋 |\r\n\r\n属性： 纯度不低于96% 净度不低于98% 芽率不低于85% 水分不高于7% |\r\n \r\n特性： \r\n  该品种抗热耐湿性强，生长快速，长势旺盛，苔骨嫩绿粗大，叶色淡绿，纤维少，风味独特，品质极佳，热带，亚热带地区全年可种植，亩产2000斤左右，是目前出口和宾馆使用的最佳品种。 |\r\n\r\n栽培方法： \r\n  长江流域及热带地区全年可种植，北方4-10月均可播种，亩产可达2000-2500斤左右，注意防治病虫害。 |  \r\n\r\n油菜心的种植法：  \r\n\r\n  \r\n\r\n      第一步 催芽夏季高温时播种需要催芽。高于25℃或者低于8℃不发芽。首先将种子浸泡于水中一晚上，然后取出放于湿纸巾上，密封放在阴凉处。有80%种子露白后开始播种。|\r\n      第二步 播种将露白的种子播撒在浇透水的花盆或庭院泥土中，覆土0.5cm，保持土壤的湿润。其他温度适宜的季节可以直接播撒油麦菜种子。覆土喷湿，保持土壤湿度，大约一周内发芽。|\r\n      第三步 育苗 移植育苗期间注意适当的光照跟水分。幼苗期不要过长时间进行高温光照。等油麦菜长至3到4片真叶时，可以进行移植。移植前浇透水，带土移植，不要伤到根部。|\r\n      第四步 管理移植后约一周可以进行施肥管理。一般是施以氮肥为主的腐熟有机肥。每一周为一周期。采摘前一周停止施肥。|\r\n      第五步 采摘当叶片数达到15片的时可开始采收，采收时间通常在早晨进行，将充分长大、厚实而脆嫩的绿色叶片用手掰下即可。', 'picture/zhongzi/datian/2.jpg'),
(3, 1, '脱毒马铃薯种薯', '田园春|15kg/箱|5.00元/kg|15 kg|甘肃省 天水市 武山县|2018-07-05', '土豆种子 脱毒马铃薯种薯 优质高产 马铃薯种子批发 原原种批发 批发运费低发货速度快，有专家团队指导种植，有全面系统的马铃薯管理方案  指导种植各阶段 全程服务 增收有保障 轻松种植|荷兰十五特性：|\r\n\r\n1、早熟：从出苗至收获生长期75—80天。植株茎杆粗壮直立、分枝少、节间短、叶片大，复叶大，叶绿色，侧小叶3～5对, 株高40—60厘米。叶色浅绿,生长势强。花冠兰紫色,花粉较多，易天然结果。块茎长椭圆形，皮色淡黄，肉色深黄，表皮光滑，芽眼少而浅，结薯集中，块茎大而整齐。|\r\n\r\n2、丰产：在我国北方山东、甘肃、宁夏、新疆等地正常生产条件下亩产量可达6000—8000斤；在高水肥生产条件下，采取高水平栽培管理，具有亩产10000斤以上的生产潜力。|\r\n\r\n3、优质：目前国内外公认的最优质菜用型品种。块茎干物质含量17.7％，淀粉12.4－14％，还原糖0.3％，粗蛋白1.67％,营养丰富，口感醇香，在全国各大中城市，特别是东南沿海地区深受广大消费者的青睐。|\r\n\r\n4、栽培：在栽培上重施底肥（亩施优质农家肥4000--5000公斤），补施追肥（结合灌水亩施含钾量较高的如硅谷15-8-28水溶肥15-20公斤），合理密植（一般每亩保苗5000株左右），及时灌水（根据土壤墒情，在北方从田间苗出齐到成熟灌4-5水，灌水时让水浸至垄高四分之三自然流过为宜，切记大水浸泡和淹过垄面），注意病虫害防治（在北方主要防治以地下害虫、晚疫病和蚜虫为主，北方的黄河灌区和南方同时注意黑胫病的防治），及时收获（在正常生长条件下，从出苗生长至70天以上，植株下部叶片落黄，即达到生理成熟，可收获；在秋季收获的马铃薯，快到成熟时为防止茎叶病害传入块茎，可进行杀秧措施。）', 'picture/zhongzi/datian/3.jpg'),
(4, 1, '荷兰十五原原种', '田园春|5-25 g/粒|0.50元/粒|1000 粒|甘肃省 天水市 武山县|2018-07-05', '一、为什么种脱毒马铃薯|\r\n\r\n马铃薯种植是采用种薯切块的方式进行的无性繁殖。这样的繁殖方式，就使得带有病毒的种薯将携带的病毒传染给了下一代。种薯的病毒会随着时代延续而逐渐 增多，严重地影响产量和商品薯质量。而马铃薯的病毒在种块上的分布并不是均匀的，薯肉上分布的多，而茎芽尖端分布的少或者没有。正是利用这一特点，切取茎 尖约十分之一毫米，进行培养基培养，就有可能脱除严重危害马铃薯的花叶病病毒、纺锤病病毒、早疫病、晚疫病、心腐病、环腐病等病毒病菌，使马铃薯恢复原来 的优良种性，产量成倍提高，商品薯质量品质明显改善。所以，最近几年种脱毒马铃薯已成为广大薯农朋友的共识。|\r\n\r\n 二、为什么要种高寒的大西北马铃薯育种？|\r\n马铃薯性喜冷凉，是喜低温的作物。其地下薯块形成和生长需要疏松透气、凉爽湿润的土壤环境。马铃薯对温度要求：块茎生长的适温是16℃~18℃，当地温高于25℃时，块茎停止生长。茎叶生长的适温是15℃~25℃，超过39℃停止生长。\r\n武山县属秦岭西北坡与陇中黄土高原西南边缘复合地带。属温带大陆性季风气候，年平均气温10.3℃，无霜期195天，年均日照2331小时，降水量500毫升左右，气候冷凉，昼夜温差大，传毒媒介少，水质和空气洁净，雨热同季，有利于块茎膨大和干物质积累，延缓退化，具有发展马铃薯产业难得的自然资源优势。\r\n\r\n所以武山无论是所处的地理环境来衡量还是从其气候特点来看，得天独厚的地理环境和自然条件使武山成为中国乃至世界马铃薯种植、繁育的最为理想的适种区之一\r\n\r\n 因此上武山及其周边乡镇就成了马铃薯种植、栽培、繁育的最为优质的主产区，武山繁育出的马铃薯种薯，以其品质优良、适应性强、抗病毒等特点省内外种植户、客户的厚爱，有着较高的市场声誉和广泛的市场影响力。|\r\n\r\n三、如何辨别现在种子市场上销售的种薯是否是真正的脱毒种薯？|\r\n\r\n首先，要对该销售企业进行考察了解，看其是否有生产脱毒马铃薯所必需的组培室、网棚，是否有生产许可证，这是两个十分必要的硬性条件，若有，接下来再看其它 条件。没有，就不要再往下看了，因为其已经对种薯质量没有最起码的保障。而天水田园春公司建有脱毒马铃薯组培实验室500平方米，原原种生产大棚5500 平方米，马铃薯种薯恒温贮藏库2000平方米，原原种生产基地1000亩。\r\n', 'picture/zhongzi/datian/4.jpg'),
(5, 1, '河北瑞峰大蒜', '瑞峰大蒜|未知|面议|无|山东省 济宁市 金乡县|长期有效', '峰牌大蒜无芯、大小均匀、绿色健康、营养价值高、增产，得到了国家的广大支持，是金乡农业局局长王卫峰联合北京农科院、北京农业大学的共同研究的成果。比普通大蒜每亩产量高达36%，种植户的佳选，商户的最佳合作者，实现共赢致富。', 'picture/zhongzi/datian/5.jpg'),
(6, 1, '富源中安2017年二代花魔芋种子', '雄升|96%/80%/96%|16.00元/kg|1 kg|云南省 曲靖市 富源县|长期有效', '富源县雄升魔芋专业化合作社成立于2007年4月，厂址在被誉为“中国魔芋第一镇”的云南富源县富村镇，公司前身是富村魔芋加工厂，创办于1998年，长年从事魔芋种植、生产、加工和魔芋种、魔芋种子、魔芋种子价格、魔芋种子批发、魔芋种子供应商、魔芋种子报价商、魔芋种子批发商、魔芋种子厂家销售。    公司一直以来在总经理曹雄昆的带领下，本着求真务实的态度，大胆探索，在富源魔芋产业“魔芋公司＋魔芋基地＋魔芋农户”的农业产业化基础建设中，充分发挥自己的一份光和热，积极推进富源魔芋产业化的发展进程。为解决当地魔芋及农副产品卖难问题，公司2009年投资3000万元，选址富村镇三孔桥新建魔芋基地及农副产品交易中心，经营面积13900平方米。年交易量5万吨，交易额1.2亿元。目前一期工程完成，已投资1760万元。交易市场是农产品流通的枢纽，市场平台的搭建，将更好地服务于三农。', 'picture/zhongzi/datian/6.jpg'),
(7, 1, '脱毒红薯苗龙9商薯19', '龙9，商薯19，苏八，济薯26，豫薯868，紫罗兰|25CM/25CM/25CM|0.06元/棵|10000 棵|河南省 开封市 尉氏县 永兴镇|长期有效', '龙薯9号|① 特征特性：|该品种顶叶绿，叶脉、脉基及柄基均为淡紫色，叶色淡绿。短蔓，茎粗中等，分枝性强，株型半直立，茎叶生长势较旺盛。单株结薯数5条左右，大中薯率高，结薯集中，薯块大小较均匀整齐，薯块纺锤形，薯皮红色，薯肉淡红色。种薯萌芽性中等，长苗较快。薯块耐贮藏性中等。耐旱、耐涝、耐瘠薄，耐寒性较强，适应性广。高抗蔓割病，高抗甘薯瘟病Ⅰ群。薯块晒干率22%左右，出粉率10%左右，食味软、较甜，属烘烤型品种。|② 品种来源：|\r\n该品种系福建省龙岩市农科所于1998年以岩薯5号为母本，金山57为父本通过有性杂交选育而成。 2004年通过福建省农作物品种审定委员会审定。|③ 产量表现：|该品种2001-2002参加甘薯新品种区试结果，两年平均鲜薯亩产3786.85公斤，比对照金山57增产47.62%； 薯干亩产805.3公斤，比对照金山57增产20.28%。生育期在90天的情况下，鲜薯亩产高达2840公斤。|④ 栽培要点：|\r\n⑴培育壮苗，采用秋薯留种。|\r\n⑵施足基肥，早追苗肥，重施"夹边肥"，后期看茎叶生长情况酌情追施"裂缝肥"或根外追肥。|\r\n⑶适期早插合理密植。早薯一般在5月上中旬插，秋薯扦插期在立秋前。扦插密度为3500～4000株/亩为宜。|\r\n⑷注意防治病虫害。由于品种茎叶生长量偏小，中后期注意防治斜纹夜蛾等食叶害虫。|\r\n⑸适时收获。全生育期不宜超过130天。技术依托，成立了汶上县脱毒甘薯种苗试验基地，每年大量培育优质甘薯种苗。\r\n中心占地面积800余亩，年储存种薯300万斤，高标准冬暖式育苗温室14座，大供棚200多个。施行了标准化、工厂化育苗。现已发展成甘薯研发薯种薯苗生产、分级加工、物流仓储、推广销售于一体的现代化企业。', 'picture/zhongzi/datian/7.jpg'),
(8, 1, '供应优质的雄玉587', '正大种业|未知|90.00元/8000粒|无|吉林省 四平市 梨树县|长期有效', '品种来源：由南通大熊种业科技有限公司和中国科学院东北地理与农业生态研究所以GA07为母本，以GB10为父本组配而成的单交种。|\r\n特征特性：幼苗叶鞘紫色。株型半紧凑，株高295厘米左右，穗位110厘米左右，成株大约21片叶。雌穗花丝浅紫色。雄穗花药浅紫色。果穗筒型，苞叶中，穗长大约21厘米，穗行数14-18行，穗轴红色，籽粒黄色，籽粒类型为半马齿型，百粒重约37.5克，出籽率85.9%。倒伏（折）率0.6(0.7)%。|\r\n经测定，籽粒容重764克/升，粗蛋白含量10.29%，粗脂肪含量3.72%，粗淀粉含量73.95%，赖氨酸含量0.30%。|辽宁省春播生育期128天，比对照郑单958短2天左右，属中晚熟玉米杂交种。|\r\n经人工接种鉴定，中抗大斑病（1-5级），抗灰斑病（1-3级），中抗弯孢菌叶斑病（1-5级），感茎基腐病（1-7级），抗丝黑穗病（病株率0.0-2.6%）。|\r\n产量表现：2013年-2014年参加辽宁省玉米品种高密中晚熟组区域试验，2013年平均亩产758.3公斤，比对照郑单958增产6.9%，2014年平均亩产934.0公斤，比对照郑单958增产13.4%。2014年参加同组生产试验，平均亩产865.7公斤，比对照郑单958增产13.3%。|\r\n栽培技术要点：适宜在中等以上肥力土壤上栽培，适宜密度为4500株/亩。|\r\n适宜地区：适宜在辽宁省内≥10℃活动积温在2800℃以上的中晚熟玉米区种植。\r\n', 'picture/zhongzi/datian/8.jpg');

