--
-- 表的结构 `nongzi-zhongzi-guaguo`
--

CREATE TABLE `nongzi-zhongzi-guaguo` (
  `id` smallint(6) NOT NULL,
  `view` int(11) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `canshu` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品参数',
  `chanping` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品说明',
  `picture` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '图片'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 转存表中的数据 `nongzi-zhongzi-guaguo`
--

INSERT INTO `nongzi-zhongzi-guaguo` (`id`, `view`, `name`, `canshu`, `chanping`, `picture`) VALUES
(1, 1, '出售优质草莓苗', '草莓|未知|面议|10000 颗|辽宁省 沈阳市|长期有效', '烟台福山区门楼镇出售上海等地北京三代甜宝草莓苗，该品种产量大，品种纯，抗病能力强，', 'picture/zhongzi/guaguo/1.jpg'),
(2, 1, '梨花粉', '达农|未知|4.50元/克|100 克|河北省 石家庄市 赵县 赵州镇|长期有效', '北方梨花粉基地供应各种梨花粉.梨树是自花授粉不结实(自交不亲和)果树,梨园必须合理配置梨花粉授粉品种或花期人工辅助授粉,才能确保丰产稳产和优质美观。在一些梨园,虽然配置了授粉品种或进行了人工授粉,但是仍出现与自花授粉相似的结实率极低的现象。本文着重介绍产生这种现象的原因和确定授粉品种的原则,为科学地选择授粉品种提供依据。|\r\n \r\n自花和异花授粉不结实的表现|\r\n \r\n对于无性繁殖的果树,同一品种内授粉称为自花授粉,自花授粉能结实的品种称为自花结实或自交亲和,自花授粉不能结实的称为自花不结实或自交不亲和,梨、苹果和杏等果树均属自交不亲和性果树。不同品种间相互授粉为异花授粉,异花授粉能结实的称为异花结实或异交亲和,否则称为异花不结实(或异交不亲和)。梨树绝大多数品种自花授粉结实率低\r\n', 'picture/zhongzi/guaguo/2.jpeg'),
(3, 1, '山东优质甜瓜苗、黄瓜苗、茄子苗', '朴华|未知|面议|无|山东省 聊城市 莘县 魏庄镇|长期有效', '山东聊城市朴华农业有限公司，位于拥有“中国蔬菜第一县、中国香瓜之乡、中国双孢菇之乡”等美誉的国家级生态示范区——山东省莘县，是一家“集传统瓜果蔬菜种植、销售、种苗培育与互联网+农业、新型农业项目开发、观光农业发展等农业新形态”相结合的新型农业公司。|\r\n\r\n公司拥有绿色无公害标准化果蔬育苗基地、专业果蔬合作社、互联网+农业运营中心等多处农业项目实施单位，截止目前已经发展成为当地“标杆性新型农业公司”。朴华农业绿色无公害标准化果蔬育苗基地，基地占地500亩，拥有职工250人，年育苗面积达200000平米，基地育有香瓜、甜瓜、茄子、黄瓜、豆角、西红柿等精品果蔬种苗。|\r\n\r\n朴华农业经过十余年的发展，不仅赢得了菜农的信赖，还常年受邀参加中国（莘县）国际瓜菜节、北京种子交易会等知名会展，会展上展销的“玛瑙翠甜瓜、圣女果、莘县香瓜”等精品果蔬更是受到了全国参会客商的一致好评。朴华农业热忱欢迎全国各地企业、客商前来参观、考察，洽谈业务。|\r\n\r\n我公司常年供应甜瓜、香瓜、樱桃西红柿、黄瓜、茄子、辣椒等优质果蔬菜种苗，也可来单代育。', 'picture/zhongzi/guaguo/3.jpg'),
(4, 1, '聊城朴华农业，甜瓜、香瓜、黄瓜圆茄优质种苗', '朴华农业|未知|面议|无|山东省 聊城市 莘县 魏庄镇|长期有效', '山东聊城市朴华农业有限公司，位于拥有“中国蔬菜第一县、中国香瓜之乡、中国双孢菇之乡”等美誉的国家级生态示范区——山东省莘县，是一家“集传统瓜果蔬菜种植、销售、种苗培育与互联网+农业、新型农业项目开发、观光农业发展等农业新形态”相结合的新型农业公司。\r\n公司拥有绿色无公害标准化果蔬育苗基地、专业果蔬合作社、互联网+农业运营中心等多处农业项目实施单位，截止目前已经发展成为当地“标杆性新型农业公司”。朴华农业绿色无公害标准化果蔬育苗基地，基地占地500亩，拥有职工250人，年育苗面积达200000平米，基地育有香瓜、甜瓜、茄子、黄瓜、豆角、西红柿等精品果蔬种苗。\r\n朴华农业经过十余年的发展，不仅赢得了菜农的信赖，还常年受邀参加中国（莘县）国际瓜菜节、北京种子交易会等知名会展，会展上展销的“玛瑙翠甜瓜、圣女果、莘县香瓜”等精品果蔬更是受到了全国参会客商的一致好评。朴华农业热忱欢迎全国各地企业、客商前来参观、考察，洽谈业务。\r\n我公司常年供应甜瓜、香瓜、樱桃西红柿、黄瓜、茄子、辣椒等优质果蔬菜种苗，也可来单代育。', 'picture/zhongzi/guaguo/4.jpg'),
(5, 1, '樱桃种子', '新鲜山樱桃种子|未知|20.00元/斤|无|全国|2018-01-04', '大量供应新鲜山樱桃种子，发芽率高，量大价格更优惠，有需要的老板与我联系13866581499', 'picture/zhongzi/guaguo/5.jpg'),
(6, 1, '雪莲果的药用价值和保健功效', '福田|未知|面议|100 棵|山东省 济南市 历城区|长期有效', '新收优质 雪莲果种苗(种球）质量保证，信誉第一\r\n雪莲果种苗,雪莲果种球,雪莲果果实,山东雪莲果,山东雪莲果种苗,雪莲果苗子,雪莲果成品,山东雪莲果种球,雪莲果种根  种植新项目雪莲果\r\n新鲜雪莲果种苗\r\n可以说今后家庭保鲜柜里没有苹果、葡萄、梨………但是不能没有雪莲果它是真正的神果，能让种植者生财致富，使食用者健身强体\r\n一种像红薯一样种植简单，春天或夏初栽植，秋季收获，高产高效，味美可口，甜脆无渣，具有较好药用和保健功效的新型速生水果-----雪莲果，由我苑2005年引进并栽植成功。\r\n雪莲果原产南美洲安第斯山脉一带的秘鲁，又叫亚贡意为“神果”因肉质雪白如玉，口味甜脆胜过雪莲，故名雪莲果，它百吃不厌，由于有较好的药用保健功效，有人还叫它的参果。植株形态像苎麻。株高2米左右，局料，向日葵属，茎顶开出五朵娇艳的太阳花凋谢表示成熟即可采挖，株产2---10公斤。亩产3—4吨种根姜状呈红色，南方在地里能自然过冬，北方秋天挖出可像红薯能贮放到来年4月，雪莲果经研究发现其极受现代人青睐的药理作用后没加之可口的美味，已在欧、美、日等达到国家引起轰动，成了是婀国明星，现国际市场价格极高，美国超市每公斤25美元（人民币217元）。台湾超市每公斤320元新台币（人民币82元）。|\r\n一、雪莲果的药用价值和保健功效|\r\n二、雪莲果的食用方法|\r\n1、生吃这是最简单而普遍的吃法，就是采挖后洗果实表面泥土，消去外皮如苹果、梨一样直接生吃食用。采挖后贮放一周后甜度增高。能像红薯一样从秋季能一直贮放到来年4月。|\r\n2、熟食。切片与鸡或排骨等肉类顿煮，是一道营养极好的佳肴。生食削下的的皮烧汤也别有风味。|\r\n3、可制成果脯、饮料、含片和作各作各种糕点的添加剂，其加工潜力极大。雪莲果易种好管，当年种好管，当年种当年收，果实外补内秀，口味香甜嫩脆，汁都无渣，可口键身，极适合现代人食用，特别是老人儿童和心脑血管疾病，消化系统病症患者，手术后病人是极好的果品，对于运动量少的办公室人员和白领阶层是百利无一害的保健果品，尤其是便秘患者更是一剂绿色药品，它温柔地调理肠胃道，可免去应用伤身害体的清肠泻药。可以说，今后家庭的保鲜柜里可以没有苹果、葡萄、梨……..但不能没有雪莲果。|\r\n雪莲果是一个横空出世的珍奇作物，它的出现，宣告一个新的保健水果时代的开始，它将在10年内会一直供不应求。它能让种植者生财致富，使用者壮身健体。', 'picture/zhongzi/guaguo/6.jpg'),
(7, 1, '中华巨龙西瓜苗', '西农八号系列|未知|0.23元/棵|1000 棵\r\n|山东省 滨州市 惠民县|长期有效', '山东西瓜苗培育基地总公司位于滨州国家农业科技园区，苗场在山东惠民济东高速（G2516）胡集北出人口与滨惠大道（S247）交汇处，占地50余亩。公司技术力量雄厚，拥有一支专业的嫁接队伍。公司以“质量第一、信誉至上、诚信经营”为宗旨，“日益创新、团结并进、合作共赢”为理念。保证以优质的服务，优良的品种为广大菜农提供最有品牌信誉的蔬菜种苗。订购种苗量大送货上门,本着免费服务。协助生产规划,科学咨询,信息传递,西瓜销售,实验示范,经验交流。|\r\n种苗供应主要品种有：宁夏中卫金城五号 甘浓宝西瓜苗，双星西瓜苗、黑美人西瓜苗、莎蜜佳一号、台湾农友蜜童|墨童无籽西瓜嫁接苗,黑帅、小兰、嘉华、黑宝,先正达西瓜苗|甜瓜苗,安徽华欣863 007西瓜苗、山东无籽西瓜苗、陕西甜王西瓜苗、河南全美2K西瓜苗、全美4K、8K西瓜苗、早春红玉西瓜苗，地雷京欣西瓜苗，西农8号西瓜苗、麒麟早佳8424西瓜苗、金花一号西瓜苗,龙卷风西瓜苗、冠龙西瓜苗及各种蔬菜苗和嫁接苗。甜瓜苗品种有：农友金玉、白玉、美浓、新玉、蜜世界、新月、玉雪、状元、金蜜、金姑娘、金香玉、玉姑、蜜莲、圣姑、爱蜜、金顶、新世纪、香妃、橙蜜、长香玉、罗蜜欧、新橙香、星甜十八20、京玉白流星、日本甜宝、绿宝甜瓜苗、天仙、玉露、天蜜、翠蜜、橙露、抗病3800、天橙、佳蜜、翠甜、翠绿等甜瓜苗。\r\n', 'picture/zhongzi/guaguo/7.jpg');

