--
-- 表的结构 `nongzi-zhongzi-shucai`
--

CREATE TABLE `nongzi-zhongzi-shucai` (
  `id` smallint(6) NOT NULL,
  `view` int(11) NOT NULL,
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `canshu` text COLLATE utf8_unicode_ci NOT NULL COMMENT '参数',
  `chanping` text COLLATE utf8_unicode_ci NOT NULL COMMENT '产品说明',
  `picture` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '图片'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 转存表中的数据 `nongzi-zhongzi-shucai`
--

INSERT INTO `nongzi-zhongzi-shucai` (`id`, `view`, `name`, `canshu`, `chanping`, `picture`) VALUES
(1, 1, '日本进口菠菜种子珍绿系列', '一品蔬菜|纯度:98%、   净度：98%  水分：10%|60.00元\\b罐|1 罐|山东省 烟台市 莱阳市 城厢街|长期有效', '日本进口杂交一代菠菜珍绿2/3/4/5号，长日照品种，耐热性好，极耐抽薹，植株直立性好，叶片圆形光滑，叶色深绿，光泽度极好，抗霜霉病，适合山东、河北、内蒙等越夏耐热茬口，同时也可以在江苏、山东10-11月较早播种，次年4月中旬采收加工，鲜食菠菜亩用种1.5-2公斤，加工菠菜亩用种0.6公斤。', 'picture/zhongzi/shucai/0.jpg\r\n'),
(2, 1, '胡萝卜种子', '一品蔬菜|98%/98%/10%|2.00元/克|100 克|山东省 烟台市 莱阳市 城厢街|长期有效', '日本进口胡萝卜种子爱木100，全国各地均可以种植，早熟丰产卖价高，杂交一代胡萝卜种子，叶片短而浓绿，播种后95-105天可以开始收获，收尾早且圆，根呈圆筒形，表皮光滑，长20~23cm，单根重250g左右，耐抽薹性较好，整齐度好，成品率高，适合北方春秋陆地，高山越夏栽培，山东春季露地亩产5000公斤，内蒙春季露地亩产4000公斤。', 'picture/zhongzi/shucai/2.jpg'),
(3, 1, '青刀豆种子5991', '一品蔬菜|98%/98%/13%|28.00元/袋|50 袋|山东省 烟台市 莱阳市 城厢街|长期有效', '刀豆栽培关键技术介绍如下。|\r\n\r\n    1、播种育苗 大刀豆种子白天温度在15℃左右，夜温在10℃左右即可开始播种，于11月上中旬采收。在山东播种期一般在4月上中旬，播种前将地粑细整平呈松软状态，将地浇湿，待土壤呈稍干稍湿时播种，每穴播1－2粒种子，将种子芽口朝下一粒一粒播下，然后覆细土1——1．5厘米，为保湿和提高地温先着地膜后播种。如果种得太深，会因氧气不足导致种子腐烂，从而造成缺苗。如果采用营养钵育茁，等长到3——4片真叶时移栽最好，行距1米，株距0．5米，搭架栽培，每667平方米栽植1300株。|\r\n\r\n    2、肥水管理 每667平方米施农家肥5000公斤，三元复合肥50公厅，钙镁磷肥50公斤，硫酸钾肥25公斤。刀豆定植缓苗后到开花结荚前要严格控制水分，防止茎蔓徒长而引起落花落荚及化荚，苗期注意进行中耕蹲苗，到植株开始甩蔓时要及时插架。在刀豆大量开花时不要浇水过多过猛，待坐住小荚时可加大浇水量和追肥量，并每667平方米追施硫酸钾复合肥30公斤，追肥分两次进行。刀豆爬满架后要经常进行摘心，确保通风透光。|\r\n\r\n    3、化学调整 刀豆生长势强，蔓旺，为避免只长蔓不坐荚这种状况，除及时摘心外，还要在花期喷洒70x10-6萘乙酸和硼砂，井视情况喷洒一定浓度的多效唑以控制疯长。 4、病由害防治 刀豆主要病害有锈病、疫病，虫害有豆荚螟、红蜘蛛等，整个生长期一般情况下可喷洒两次甲基托布津800倍，三次阿维菌素1500倍。播种前撒施呋喃丹以防地下害虫。', 'picture/zhongzi/shucai/3.jpg'),
(4, 1, '日本青秀水果黄秋葵种子', '一品蔬菜|未知|0.06元/粒|1000 粒|山东省 烟台市 莱阳市 城厢街|长期有效', '日本青秀黄秋葵特性描述|\r\n     青秀系引进日本品种，果色鲜绿色，肉质脆嫩品质上佳，嫩果切面呈五角形，果柄有莲座美观漂亮，果形细长光滑顺直，秀品率高。生长旺盛，该品种耐热耐寒性强，容易栽培管理，市场极受欢迎的黄秋葵更新换代品种。|\r\n \r\n青秀黄秋葵栽培要点|\r\n \r\n青秀黄秋葵生长最佳适温：15-32℃，在第1-2果收获期开始追肥，生育初期应注意合理控制肥水，预防立枯病。参照当地栽培经验及习惯栽培管理。|\r\n\r\n注意事项|\r\n \r\n      1．本包装之图片及成熟天数，仅供用户参考，不作实物样本及作物成熟天数，因受播种时间、气候、土壤及栽培管理等因素影响会有差异，应在小面积试种成功后，方可播种。惠购者请适时、适地、适法栽培管理，以获得最佳收益。|\r\n      2．该品种可以反季节栽培。|\r\n      3．种子播种时请详细参阅栽培资料。\r\n', 'picture/zhongzi/shucai/4.jpg'),
(5, 1, '水果秋葵种子青云一号有莲座的秋葵品种', '一品蔬菜|未知|0.05元/粒|0.05元/粒|1000 粒|山东省 烟台市 莱阳市 城厢街|长期有效', '青云一号水果秋葵种子，品种特点：早生，植株中高，叶形较小，缺刻深，适宜密集栽培。果形细长丰满，荚果五角形，有莲座，果色特浓绿，果面无刚毛，荚果老化晚，形色优美，品质优良。高温期播种，座果节位在第7-8节起，低温期播种，座果节位在第4节起，适收期单荚重14克左右，嫩荚横径2厘米左右。', 'picture/zhongzi/shucai/5.jpg'),
(6, 1, '精品补血天骄莴笋种子', '南箭|未知|面议|无|湖北省 武汉市 武昌区 中华路街|长期有效', '产品名称： 精品补血天骄莴笋种子 |\r\n\r\n优点： 尖叶 特抗低温 清香爽口 |\r\n \r\n包装： 10G/塑料袋 |\r\n\r\n属性： 纯度不低于95% 净度不低于98% 芽率不低于80% 水分不高于8%| \r\n\r\n特性： \r\n  该品种是我所从“挂丝红”莴笋的变异培育而成，具有清香味特浓，肉质清脆，抗寒及抗病能力特强的特点。株形直立紧凑，叶绿色，绿中有紫红色线条，皮淡绿带有紫红晕斑，该品种特耐肥水，若在肥水充足条件下，其产量更高。 |\r\n\r\n栽培方法：\r\n  长江流域8-10月播种最佳，播种温度15-25度，亩用种量60克，苗床用地0.8厘地，苗龄25天，注意稀播，培养壮苗，大田定植40*35厘米，切记弱苗不能定植。莴笋拔节初期（开盘期）采用清水压盘2次或施用“莴笋膨大素”以防止早抽薹，增加产量。提前早防“霜霉病”“菌核病”与蚜虫、潜叶螟的危害。早秋高温时发芽非常困难，可采用冰块桶中发芽、冰箱发芽、悬挂深井发芽等技术，总之低温处理，温度为2-5度，时间3-5天或冻处理零下3-5度，时间一昼夜，再放于温度为15-20度，时间3-5天  ', 'picture/zhongzi/shucai/6.jpg'),
(7, 1, '三青郞A四季大尖叶莴笋种子', '南箭|未知|面议|无|湖北省 武汉市 武昌区 中华路街|长期有效', '产品名称： 三青郞A四季大尖叶莴笋种子 |\r\n\r\n优点： 四季大尖叶莴笋，原种级种子 |\r\n\r\n包装： 15G/塑料袋 |\r\n\r\n属性： 纯度不低于95% 净度不低于98% 发芽率不低于80% 水份不高于7%  |\r\n\r\n特性： \r\n  原种级种子，多年定向培育，最新选育新一代春秋型莴笋精品，叶片大披针形，叶绿，笋棒粗大，茎皮嫩绿，笋肉翠绿清香，植株生长强势，抗病力优于对照，适宜春、秋季栽培，部分区域可四季栽培。 |\r\n\r\n栽培方法： \r\n  1、该品种适宜长江流域露地种植，春季1月10日-3月20日，秋季7月20日-9月10日，生育期为105天左右。莴笋种发芽最适宜温度15℃-20℃，温度高于22℃或低于10℃均不易发芽。因此，不用季节播种应采取相应措施调节温度，以利于种子正常发芽。 |\r\n  2、该品种属于春秋季节栽培主打品种，若温度低于10℃，应进行保温催芽、育苗（如用塑料薄膜小拱棚）以获得适龄壮苗。|\r\n  3、该品种茎棒粗大，生长迅速整齐，建议育苗移栽，5-6片叶左右带土移栽，亩栽4000-5000株。 |\r\n  4、加强水肥管理，低温、湿度大、雾气重时期，田间应保持干燥，并用磷酸二氢钾0.2%及精品硼肥0.2%（水溶液）进行叶面施肥3-5次。 |\r\n  5、生长管理过程中，需提前防治霜霉病、灰霉病、菌核病的发生以及蚜虫的危害。|6、若遇特别低温影响生长点生长时注意保温，否则会促进腋芽生长成为手指大的细笋，影响商品性。| \r\n  7、适时采收，以获良好的市场经济效益。 |\r\n\r\n\r\n\r\n主要价值： \r\n   莴笋能改善消化系统功能。因其味道清新且略带苦味，可刺激分泌，增进食欲。莴笋中含有的大量纤维素，能够促进人体的肠壁蠕动，防治便秘。莴笋的其乳状浆液，能增强胃液、消化腺的分泌和胆汁的分泌，能迅速帮助人体排出宿便和毒素以及浊气，起到清肠、减肥、瘦身的作用。\r\n莴笋有润发、利尿、通乳的功效。莴笋中钾的含量远远高于钠含量，能促进排尿，维持水平衡。莴笋也有通乳的功效，《本草纲目》记载，李时珍曾用莴笋加酒，煎水服用来治疗产后乳汁不通。因缺钾而脱发者，经常食用莴笋，可以令秀发乌黑、浓密、顺滑。\r\n莴笋中所含的氟元素，可参与牙釉质和牙本质的形成，参与骨骼的生长，另外，莴笋中还含有铁、钙等矿物质，对儿童换牙、长牙很有好处。莴笋中的含碘量高，有利于人体基础代谢、心智、体格发育以及情绪调节，因此莴笋具有镇静的作用，经常食用有助于消除紧张，帮助睡眠。\r\n莴笋具有调节神经系统功能的作用，其所含有机化含物中富含人体可吸收的铁元素，可治疗缺铁性贫血，莴苣的热水提取物可以抑制某些癌细胞，所以莴笋是防癌抗癌的保健蔬菜\r\n', 'picture/zhongzi/shucai/7.jpg'),
(8, 1, '台农一号黑皮冬瓜种子', '台农|未知|面议|无|湖北省 武汉市 武昌区 中华路街|长期有效', '产品名称： 台农一号黑皮冬瓜种子|\r\n\r\n包装： 10G/塑料袋|\r\n\r\n属性： 纯度不低于95% 净度不低于99% 芽率不低于75% 水分不高于8% |\r\n\r\n特性： \r\n  抗病，耐热及耐寒性能强，叶型中大，侧蔓多，结瓜密，一般以主蔓结瓜为宜，瓜子长55-60公分，横茎23-27公分，通常瓜重20-30公斤，瓜似炮弹型，皮墨绿色有光泽，肉质厚而白，子室小，耐贮运，适宜中国大陆各地区春，夏，秋三季种植，春播期12月至翌年3月，夏播70-80天，秋播期6-7个月，春播生长期120-150天，秋播生长期80-110天均可收获。|\r\n\r\n栽培方法：\r\n  种子萌芽困难，一般发芽不整齐，为了培育成壮苗，宜采用浸种20-24小时候放在30-35度处催芽。春季早熟栽培保护地育苗，（晚）夏，（早）秋，露地保护育苗，移植前多培基肥，瓜地要利于排水，结瓜时多施肥多喷雾可湿性粉剂退菌药。成瓜后要立即吊瓜及垫草。株行距100*150公分，一般管理参照当地的种植习惯，敬请参考“顺和隆”冬瓜种子详细栽培资料，在粤东地区每亩预种量为200-300克。特别提示：用920与白酒稀释后浸种3-4小时，再进行催芽。', 'picture/zhongzi/shucai/8.jpg');

