  `name` varchar(25) COLLATE utf8_unicode_ci NOT NULL COMMENT '作物名字',
  `title` varchar(150) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `chapter` text COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` longtext COLLATE utf8_unicode_ci NOT NULL COMMENT '每个章节内容',
  `picture` text COLLATE utf8_unicode_ci NOT NULL COMMENT '图片',
  `video` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '视频地址'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='果树表';

