--
-- 表的结构 `teach-shucai`
--

CREATE TABLE `teach-shucai` (
  `id` smallint(6) NOT NULL,
  `view` int(11) NOT NULL,
  `name` varchar(25) COLLATE utf8_unicode_ci NOT NULL COMMENT '蔬菜作物名字',
  `title` varchar(150) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `chapter` text COLLATE utf8_unicode_ci NOT NULL COMMENT '二级标题',
  `content` longtext COLLATE utf8_unicode_ci NOT NULL COMMENT '内容',
  `picture` text COLLATE utf8_unicode_ci NOT NULL COMMENT '图片地址',
  `video` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '视频地址'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='蔬菜表';

