--
-- 表的结构 `video-hot`
--

CREATE TABLE `video-hot` (
  `id` smallint(6) NOT NULL,
  `view` int(11) NOT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` text COLLATE utf8_unicode_ci NOT NULL COMMENT '正文',
  `picture` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '视频图片',
  `video` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '视频'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='视频热门';

--
-- 转存表中的数据 `video-hot`
--

INSERT INTO `video-hot` (`id`, `view`, `title`, `content`, `picture`, `video`) VALUES
(1, 1, '简单大棚西瓜种植教学视频', '西瓜，西瓜堪称“瓜中之王”，原产于非洲。唐代引入新疆，五代时期引入中土。属葫芦科，有多个种子。 中国南北皆有西瓜栽培，西瓜是一种双子叶开花植物，植株形状像藤蔓，叶子呈羽毛状。它所结出的果实是瓠果，为葫芦科瓜类所特有的一种肉质果，是由3个心皮具有侧膜胎座的下位子房发育而成的假果。西瓜主要的食用部分为发达的胎座。果实外皮光滑，呈绿色或黄色，果瓤多汁为红色或黄色，罕见白瓤。西瓜性寒，味甘甜|。它有清热解暑、生津止渴、利尿除烦的功效。有助于治胸膈气壅，满闷不舒服，小便不利，口鼻生疮，暑热，中暑，解酒毒等症。皮还可以做菜、入药等。 世界上最小的西瓜产于南美洲，学名“佩普基诺”，是一种营养价值很高的高档水果，外表与普通西瓜无异，3厘米长、直径2厘米。外皮柔滑细嫩，内瓤为青绿色。', 'picture/hot/1.jpg', 'video/zhongzhi/1.mp4'),
(2, 1, '新疆特色蔬菜走向全国', '新疆地人物博，资源丰富。这些资源中包括着丰富的土特产资源，有些土特产在全国名列前茅，有的还成为珍品，有闻名中外的和田玉、宝石和用和田玉精雕细琢、玲珑剔透成的各种玉雕；有四季飘香的新疆哈密瓜、伽师瓜、吐鲁番无核葡萄等瓜果；有闻名遐迩的新疆棉花、啤酒花、伊犁薰衣草。', 'picture/hot/2.jpg', 'video/zhongzhi/1.mp4'),
(3, 1, '饮品新消费下的鲜果产业', '从百香果到橙子，在消费需求拉动下，水果深加工产品越来越多样化，产业链也越来越细、越来越长。产业链扩张的过程，也是价值链延伸的过程，水果种植要增加收入，就必须深度参与这个产业链的扩张，生产符合消费新需求的产品，用掌握标准、产业融合等手段，把更多的产业环节掌握在自己的手里，提高市场话语权。沿着产业链搞生产，顺着价值链去挣钱。', 'picture/hot/3.jpg', 'video/zhongzhi/1.mp4'),
(4, 1, '油菜“瘦身”为哪般', '油菜是我国重要的油料作物，每年可为我国提供470万吨左右食用植物油和800万吨以上优质蛋白饲料，关系着一亿多油菜种植农民的基本生计和收入增长。然而，近两年一些油菜主产区的村民正在逐渐放弃种植油菜，为什么会出现这种情况呢?眼下正是长江流域油菜收获的季节，那么今年的油菜收成如何?广大种植户又是怎么看待油菜生产的?整个产业面临着什么样的困难?', 'picture/hot/4.jpg', 'video/zhongzhi/1.mp4'),
(5, 1, '“麦痴”院士的育种故事', '我国第一个替代进口的面包小麦长啥样?用它做的面包好不好吃?超高产品种济麦22到底有多大的能耐，竟能连续7年成为全国种植面积最大的品种?看赵振东院士怎样书写下这些小麦传奇！！', 'picture/hot/5.jpg', 'video/zhongzhi/1.mp4'),
(6, 1, '把好细节种出好葡萄', '新疆省家口市的鹿县是中国葡萄之乡，是我国最适合葡萄生长的种植区之一。但近几年，这里的葡萄虽然有产量，销量却是一年不如一年。其中原因就是当地种植户在种植葡萄的细节上存在很多误区。', 'picture/hot/6.jpg', 'video/zhongzhi/1.mp4'),
(7, 1, '沙漏地里种葡萄', '种葡萄，只铺半边地膜，为什么每亩就能省一千元?用竹竿打枝梢，为什么还能增产两成?看似简单的创新，怎么成了降成本增效益的利器?', 'picture/hot/7.jpg', 'video/zhongzhi/1.mp4'),
(8, 1, '神了 施肥也能走穴了', ' 水稻传统施肥浪费大，怎么才能用管道顺利把肥料送到稻秧根部。一块木板的启发，如何让传统农业和现代机械化相互结合。', 'picture/hot/8.jpg', 'video/zhongzhi/1.mp4'),
(9, 1, '一样的大棚 不一样的功能', '江西乐平有悠久的蔬菜生产历史，它处在南北过渡地带，当地人利用自己的技术，发挥气候上的优势，抓住空档期，成就了名副其实的江南菜乡！当地利用大棚提温，茄子二月下种，三月底就能上市。', 'picture/hot/9.jpg', 'video/zhongzhi/1.mp4');

