--
-- 表的结构 `video-sannong`
--

CREATE TABLE `video-sannong` (
  `id` smallint(6) NOT NULL,
  `view` int(11) NOT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` text COLLATE utf8_unicode_ci NOT NULL COMMENT '正文',
  `picture` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '视频图片',
  `video` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '视频'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='视频热门';

--
-- 转存表中的数据 `video-sannong`
--

INSERT INTO `video-sannong` (`id`, `view`, `title`, `content`, `picture`, `video`) VALUES
(1, 1, '打好扶贫“组合拳” 精准发力拔“穷根”', '“扶贫必先扶智，对于基础教育相对落后的东方而言，教育扶贫是最有效、最计长远的扶贫方式。”在东方市教育局局长邢卫民看来，该市今年在教育扶贫方面的力度空前。今年9月1日，东方市将各乡镇330名建档立卡贫困户家庭子女(小学四年级至初中三年级)，全部安排到市区的两所思源学校读书，不仅不收一分钱，每月还发400-500元不等的生活补贴，让贫困家庭的孩子享受最好的教育资源。', 'picture/sannong/1.jpg', 'video/zhongzhi/1.mp4'),
(4, 1, '农交会上看五年——让绿色创造财富', '农业和农村经济对全省的社会稳定、经济繁荣、人民生活的改善起了强大的支撑作用。', 'picture/sannong/4.jpg', 'video/zhongzhi/1.mp4'),
(6, 1, '农业发展需要农机 农机发展需要教师', '农业发展需要农机，农机发展需要教师。', 'picture/sannong/6.jpg', 'video/zhongzhi/1.mp4'),
(7, 1, '推进农村创业创新 加快培育农村发展新动能', '推进农业供给侧结构性改革，关键在完善体制、创新机制。要紧紧围绕使市场在资源配置中起决定性作用和更好发挥政府作用，加快深化农村改革，理顺政府和市场的关系，全面激活市场、激活要素、激活主体。', 'picture/sannong/7.jpg', 'video/zhongzhi/1.mp4'),
(3, 1, '国家助学贷款照亮寒门学子上学路', '今年，我市将首次启动生源地信用助学贷款，从7月20日起开始接受贷款申请。这就意味着，被省外大学录取的新疆贫困学生，可以在家门口申请贷款，助圆大学梦。', 'picture/sannong/3.jpg', 'video/zhongzhi/1.mp4'),
(5, 1, '农业部：努力打造绿色高产高效创建升级版', '　9月5日，农业部在宁夏召开全国绿色高产高效创建推进落实会，交流各地绿色高产高效创建进展及成效，安排部署下一步重点工作。会议认为，顺应我国农业农村经济发展的新趋势新要求，农业部启动“绿色高产高效创建年”活动，是提升创建层次和水平的主动作为、顺势而为，各地要大力推进技术模式创新和服务机制创新，努力打造绿色高产高效创建升级版，引领农业转型升级和绿色发展。', 'picture/sannong/5.jpg', 'video/zhongzhi/1.mp4'),
(2, 1, '第十五届中国国际农产品交易会开幕', '中国国际农产品交易会（简称农交会）是经国务院批准、农业部主办的大型农业行业盛会，已成功举办十四届，现已成为我国农业领域规模最大、规格最高、最具权威和影响力的品牌展会，为展示农业发展成就、促进农产品贸易、推广先进技术、推动国际农业交流合作提供了重要平台。', 'picture/sannong/2.jpg', 'video/zhongzhi/1.mp4');

