--
-- 转存表中的数据 `video-zhongzhi`
--

INSERT INTO `video-zhongzhi` (`id`, `view`, `title`, `content`, `picture`, `video`) VALUES
(1, 1, '简单西瓜种植教学视频', '西瓜,堪称“瓜中之王”，原产于非洲。唐代引入新疆，五代时期引入中土。属葫芦科，有多个种子。 中国南北皆有西瓜栽培，西瓜是一种双子叶开花植物，植株形状像藤蔓，叶子呈羽毛状。它所结出的果实是瓠果，为葫芦科瓜类所特有的一种肉质果，是由3个心皮具有侧膜胎座的下位子房发育而成的假果。西瓜主要的食用部分为发达的胎座。果实外皮光滑，呈绿色或黄色，果瓤多汁为红色或黄色，罕见白瓤。西瓜性寒，味甘甜；它有清热解暑、生津止渴、利尿除烦的功效；有助于治胸膈气壅，满闷不舒服，小便不利，口鼻生疮，暑热，中暑，解酒毒等症。皮还可以做菜、入药等。 世界上最小的西瓜产于南美洲，学名“佩普基诺”，是一种营养价值很高的高档水果，外表与普通西瓜无异，3厘米长、直径2厘米。外皮柔滑细嫩，内瓤为青绿色。', 'picture\\zhongzhi\\1.jpg', 'video/zhongzhi/1.mp4'),
(8, 1, '甜瓜种植教学', '甜瓜，葫芦科植物的果实肉质，具多室及多种子，和浆果很像，但是这些果实的花托与外果皮愈合，常形成厚厚的外皮，在分类上特别称之为“瓜果”，香瓜就是一个典型的例子。\r\n各种香瓜均含有苹果酸、葡萄糖、氨基酸、甜菜茄、维生素C等丰富营养。全国各地广泛栽培，世界温带至热带地区也广泛栽培。', 'picture\\zhongzhi\\8.jpg', 'video/zhongzhi/1.mp4'),
(5, 1, '玉米种植教学', '玉米是一年生雌雄同株异花授粉植物， 植株高大， 茎强壮， 是重要的粮食作物和饲料作物， 也是全世界总产量最高的农作物， 其种植面积和总产量仅次于水稻和小麦。玉米一直都被誉为长寿食品， 含有丰富的蛋 白质、 脂肪、 维生素、 微量元素、 纤维素等， 具有开发高营养、 高生物学功能食品的巨大潜力。但由于其遗传性较为复杂， 变异种类丰富， 在常规的育种过程中存在着周期过长、 变异系数过大、 影响子代生长发育的缺点， 而现代生物育种技术不但克服了上述缺点和不足， 同时也提高了育种速度和质量。', 'picture\\zhongzhi\\5.jpg', 'video/zhongzhi/1.mp4'),
(6, 1, '棉花种植教学', '棉花，植物的种籽纤维，原产于亚热带。植株灌木状，在热带地区栽培可长到6米高，一般为1到2米。花朵乳白色，开花后不久转成深红色然后凋谢，留下绿色小型的蒴果，称为棉铃。棉铃内有棉籽，棉籽上的茸毛从棉籽表皮长出，塞满棉铃内部，棉铃成熟时裂开，露出柔软的纤维。纤维白色或白中带黄，长约2至4厘米（0.75~1.5寸），含纤维素约87～90%，水5~8%，其他物质4~6%。棉花产量最高的国家有中国、美国、印度等。', 'picture\\zhongzhi\\6.jpg', 'video/zhongzhi/1.mp4'),
(2, 1, '白萝卜种植教学视频', '白萝卜，根茎类蔬菜，十字花科萝卜属植物。至今种植有千年历史，在饮食和中医食疗领域都有广泛应用。清乾隆庚午年（公元1750年）编修的《如皋县志》载：“萝卜，一名莱菔，有红白二种，四时皆可栽，唯末伏初为善，破甲即可供食，生沙壤者甘而脆，生瘠土者坚而辣。”其色白，属金，入肺，性甘平辛，归肺脾经，具有下气、消食、除疾润肺、解毒生津，利尿通便的功效。主治肺痿、肺热、便秘、吐血、气胀、食滞、消化不良、痰多、大小便不通畅等，白萝卜很适合用水煮熟后，喝萝卜水，放点白糖，可以当做饮料饮用，对消化和养胃有很好的作用', 'picture\\zhongzhi\\2.jpg', 'video/zhongzhi/1.mp4'),
(7, 1, '哈密瓜种植教学', '哈密瓜，是甜瓜的一个转变。又名雪瓜、贡瓜，是一类优良甜瓜品种，果型圆形或卵圆形，出产于新疆。味甜，果实大，以哈密所产最为著名，故称为哈密瓜。因为味道甜美。据史料记载，清朝康熙年间，哈密王把甜瓜作为礼品向朝廷进贡，“哈密瓜”便由此得名，并成为新疆甜瓜的总称。', 'picture\\zhongzhi\\7.jpg', 'video/zhongzhi/1.mp4'),
(4, 1, '猴头菇种植教学', '猴头菇，又叫猴头菌，只因外形酷似猴头而得名。猴蘑，猴头，猴菇，是中国传统的名贵菜肴，肉嫩、味香、鲜美可口。是四大名菜（猴头、熊掌、燕窝、鱼翅）之一。有“山珍猴头、海味鱼翅”之称。这种齿菌科的菌类，菌伞表面长有毛茸状肉刺，长约1～3厘米，它的子实体圆而厚，新鲜时白色，干后由浅黄至浅褐色，基部狭窄或略有短柄，上部膨大，直径3.5～10厘米，远远望去似金丝猴头，故称“猴头菇”，又像刺猬，故又有“刺猬菌”之称。猴头菌是鲜美无比的山珍，菌肉鲜嫩，香醇可口，有“素中荤”之称。', 'picture\\zhongzhi\\4.jpg', 'video/zhongzhi/1.mp4'),
(3, 1, '葡萄种植教学', '西瓜,堪称“瓜中之王”，原产于非洲。唐代引入新疆，五代时期引入中土。属葫芦科，有多个种子。 中国南北皆有西瓜栽培，西瓜是一种双子叶开花植物，植株形状像藤蔓，叶子呈羽毛状。它所结出的果实是瓠果，为葫芦科瓜类所特有的一种肉质果，是由3个心皮具有侧膜胎座的下位子房发育而成的假果。西瓜主要的食用部分为发达的胎座。果实外皮光滑，呈绿色或黄色，果瓤多汁为红色或黄色，罕见白瓤。西瓜性寒，味甘甜；它有清热解暑、生津止渴、利尿除烦的功效；有助于治胸膈气壅，满闷不舒服，小便不利，口鼻生疮，暑热，中暑，解酒毒等症。皮还可以做菜、入药等。 世界上最小的西瓜产于南美洲，学名“佩普基诺”，是一种营养价值很高的高档水果，外表与普通西瓜无异，3厘米长、直径2厘米。外皮柔滑细嫩，内瓤为青绿色。', 'picture\\zhongzhi\\3.jpg', 'video/zhongzhi/1.mp4');

