--
-- 表的结构 `wrom-shucai`
--

CREATE TABLE `wrom-shucai` (
  `id` int(11) NOT NULL,
  `view` int(11) NOT NULL,
  `title` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` text COLLATE utf8_unicode_ci NOT NULL COMMENT '内容',
  `time` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '发表时间',
  `picture` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '图片地址'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='虫-蔬菜';

--
-- 转存表中的数据 `wrom-shucai`
--

INSERT INTO `wrom-shucai` (`id`, `view`, `title`, `content`, `time`, `picture`) VALUES
(1, 1, '豇豆根腐病的防治', '问：这是架豆什么病|答：|专家一：根腐病用恶霉灵加枯草芽孢杆菌灌根防治或用甲基立枯磷灌根防治或用生根剂加恶霉灵加甲霜灵灌根防治，用药做好二次稀释，7到10天用药一次|专家二：根腐病，是土壤病菌消毒处理不当或重茬导致。病菌感染根系茎杆，植株枯萎死亡，防治方法：用恶霉灵，枯草芽孢杆菌，多抗霉素灌根防治，间隔十天一次连防两次，注意水量控制好，注意浓度|专家三：根腐病属于土传播疾病，由土壤中残留病菌传染导致 建议用25%甲霜•霜霉威125-187克/亩喷雾 50%王铜•甲霜灵100-125克/亩喷雾 56%甲硫•恶霉灵600-800倍液灌根 10亿个/克枯草芽孢杆菌150-200克/亩喷雾 20%二氯异氰脲酸钠300-400倍液灌根 30%精甲•恶霉灵15-18毫升/亩喷雾 3%甲霜•恶霉灵500-700倍液灌根 可以用以上药剂+枯草芽胞杆菌+生根剂喷淋茎杆灌根防治|专家四：根腐病。病害表现：茎基腐病或根部病变或茎杆维管束发生病变腐烂。。防治方法：对土壤进行消毒处理。用甲基立枯磷2500倍液或络氨铜2000倍液或恶霉灵1500倍液，枯草芽孢杆菌2500倍液或噻菌铜2500倍液或咯菌腈2000倍液，四霉素2800倍液或五氯硝基苯3000倍液灌根防治，间隔七到十天一次，连防两到三次。。注意事项：注意二次稀释现配现用', '2017-09-20', 'picture\\wrom\\shucai\\1.jpg'),
(2, 1, '芹菜蓟马怎么防治？', '问：请老师看看芹菜是什么病|答：|专家一：考虑蓟马危害，可以用吡蚜·呋虫胺，艾绿士+噻虫嗪， 啶虫眯+联苯菊酯，乙基多杀霉素，烯啶虫胺等加有机硅交替使用喷雾防治。|专家二：蓟马危害，建议用吡蚜酮，吡虫琳，烯啶虫胺，阿维啶虫咪，噻虫嗪，乙基多杀霉素，联苯菊酯等喷雾防治|专家三：刺吸害虫危害 建议 用5%阿维啶虫眯15-20毫升/亩喷雾 50%烯啶虫胺5-10克/亩喷雾 50%抗蚜威10-18克/亩喷雾 40%烯啶吡蚜酮10-15克/亩喷雾 20%呋虫胺20-40克/亩喷雾 70%吡虫啉2-3克/亩喷雾 200克/升丁硫克百威75-125毫升/亩喷雾 25%噻虫嗪8-15克/亩喷雾 5%多杀霉素40-50毫升/亩喷雾 60克/升乙基多杀菌素10-20毫升/亩喷雾 可以用以上等药剂交替使用防治|专家四：蓟马为害，用烯啶虫胺，噻虫嗪，乙基多杀霉素等交替使用防治', '2017-09-20', 'picture\\wrom\\shucai\\2.jpg'),
(3, 1, '春季大棚空心菜的虫害防治', '空心菜除了有着食用作用，有着自身的营养价值之外，还有着解毒功效，例如误食野菌、毒菇、毒鱼藤、断肠草以及砒霜中毒等，除此之外就是肺热咳血，鼻出血或尿血，浮肿腹水，小便不利等症状都有这一定的治疗功效。|早春大棚中，空心菜一般容易发生虫害—蚜虫、粉虱、红蜘蛛、茶黄螨等。防治方法有物理性防治方法、生物防治方法和药剂防治方法。|物理性防治方法：大棚防风口要安装防虫网，棚内悬挂黄板来诱杀蚜虫。生物防治方法：利用天敌来防治虫害，在蚜虫、红蜘蛛、茶黄螨发生初期定期、定量放捕食蚜虫的瓢虫、捕食螨、丽蚜小蜂等。|药剂防治：虫害初期利用：烕蚜毙、高效溴氰菊酯、阿维菌素等防治蚜虫。利用哒螨灵、克螨特等防治红蜘蛛、茶黄螨。', '2017-09-20', 'picture\\wrom\\shucai\\3.jpg'),
(4, 1, '大葱葱斑潜蝇的防治方法', '大葱葱斑潜蝇又称潜叶蝇，叶蛆。幼虫终生在叶内曲折穿行，取食大葱叶肉，叶面上可见到迂回曲折的蛇行隧道，大葱被取食后，只留两层白色透明的叶表皮，严重时，一叶被多条虫危害，叶肉被大量取食，严重影响光合作用的进行，严重时可使全叶枯萎，产量下降。|一般1年发生3-5代，以蛹在被害大葱叶内和土中越冬。5月上旬为成虫发生盛期，成虫在傍晚把卵散产在大葱叶片组织内，卵呈白色，4-5天后孵化出幼虫，即开始在叶内取食危害，6月份为危害盛期，老熟幼虫在隧道一端化蛹，化蛹后穿破叶表皮开始羽化。因葱潜叶蝇幼虫一直在叶内危害，空气湿度对其生长发育影响不大，但温度影响较大，天气炎热时发育不良，危害较轻。|防治方法|(1)清洁田园，消灭虫源。在栽植前和收获后都要及时清除田间残叶、枯叶，带出田外烧毁和深埋，并深翻、冬灌，消灭越冬虫源。|(2)诱杀成虫。可用红糖、醋各100克，加水1000克煮沸，加入40克敌百虫，调至均匀，然后均匀的拌在40千克干草和树叶上，撒入田间诱杀成虫。|(3)化学防治。应抓住在成虫产卵盛期或幼虫孵化初期喷药效果最好的时期，杀死成虫和卵。6月上旬开始每周喷洒1次杀虫剂和杀卵剂。也可用烟草石灰水，杀死卵粒，制作方法：用烟叶0.5千克，浸泡在20千克清水中一昼夜，然后过滤掉烟叶和烟渣。然后用10千克水将0.25千克生石灰化成石灰乳，再过滤.使用前将烟叶水和石灰乳混合均匀，即可使用。成虫发生盛期用40%乐果乳油1000-1500倍液，50%敌百虫800倍液，25%灭杀毙乳油6000倍液轮换使用，交替喷洒。每种药剂在大葱的1个生长季只准喷洒1次。并且在收前10-15天停止用药。', '2017-09-20', 'picture\\wrom\\shucai\\4.jpg'),
(5, 1, '甜菜苗期虫害的防治', '甜菜增产的关键措施之一是保全苗,而苗期害虫直接影响着甜菜的全苗。每年因其为害缺 苗均在10%以上,严重时高达20～30%,甚至重种或改种,对甜菜高产、稳产影响很大。为了确保甜菜亩满苗全,丰产丰收,菜农们需要加强管理提早防治。下面，小编就为大家介绍下甜菜苗期虫害的防治。|甜菜苗期虫害的防治| 一、甜菜苗期虫害| 1、蛴螬|是金龟子幼虫的总称，种类很多，其对寄主植物具有选择性，多寄生于大豆、小麦、玉米等作物上取食和产卵，故这三种茬口种甜菜时会发生较重的蛴螬危害。春季土壤化冻时期有利于蛴螬生长，因此6月中旬危害苗期甜菜。|2、地老虎|俗称截虫或切根虫。其中小地老虎为数量最多的苗期病害，多发生在定苗前后，易造成甜菜缺苗、断条，从而造成保苗株数不足使根产量降低。|3、象甲|又叫象鼻虫，危害甜菜的象甲主要有：东方甜菜黑象甲、甜菜灰象甲、亚洲灰象甲、甜菜茎象甲、普通甜菜象甲、甜菜象甲、甜菜长柄象甲、二斑尖眼象甲、三北甜菜象甲、蒙古灰象甲、条纹象甲。|4、跳甲|属鞘翅目、叶甲科，跳甲种类很多，以甜菜跳甲和黄条跳甲为主。多发生在5月上、中旬，甜菜幼苗出土后，跳甲便是危害盛期。| 5、甘蓝夜蛾|又称甘蓝叶盗虫，属鳞翅目。是多年来均发生的虫害，一般一年发生1～2代，分别于5月末6月初、8月末至9月上旬发生。若第一代未及时防治或防治不彻底，则第二代会造成严重的危害。由于叶片大面积被吃光只剩下叶脉，严重影响干物质积累及光合作用，从而造成甜菜根产量严重减产，含糖也有不同程度的降低。|二、防治方法|1、秋季深翻可直接杀死越冬虫蛹，降低来年的虫口基数。|2、可在苗期撒敌百虫毒饵。利用糖醋液或黑光灯诱杀成虫等方法防治地老虎、金针虫、蛴螬等害虫。|3、药剂防治：| ①苗期喷洒2.5%漠氰菊酯。或20%速灭杀丁3000倍液。或90%敌百虫1000倍液，防治象鼻虫、甜菜夜蛾及甜菜螟等害虫。|②用灭扫利2000-2500倍液喷杀甜象甲、地老虎。蛴螬6月中旬危害|③用天诺丙溴.辛或天诺毒.辛1500倍液+润周6号3000倍液+乐乐逗200倍液，在5月上、中旬喷施防治跳甲，在6月上中旬喷施防治甘蓝夜蛾第一代幼虫，也可用菌杀敌（Bt）600-800倍液喷雾。', '2017-09-20', 'picture\\wrom\\shucai\\5.jpg'),
(6, 1, '生姜的虫害防治方法', '生姜别名有姜根、百辣云、勾装指、因地辛、炎凉小子、鲜生姜、蜜炙姜。姜的根茎（干姜）、栓皮（姜皮）、叶（姜叶）均可入药。生姜在中医药学里具有发散、止呕、止咳等功效。|生姜的虫害防治方法|生姜在进行种植的过程中容易受姜螟、异形眼覃蚊的为害。|1、姜螟（玉米螟）。是为害生姜的主要害虫，它的食性很杂，为害时连续咬食嫩茎，并钻到茎中继续为害，故又叫钻心虫。|防治措施|清洁田园，把收获后遗留在田间虫蛀的断株、枯叶等清理干净、烧毁。剥除虫叶、虫鞘，在不影响生姜生长情况下，把附有虫害的姜叶、地上茎剥除掉，烧毁。叶面喷洒90%敌百虫800倍液，或50%马拉硫磷1000倍液，或敌杀死2000-3000倍液，也可以用以上药液注入地上茎的虫口。|2、异形眼覃蚊是生姜贮藏期的主要害虫，幼虫俗称姜蛆，也能为害田间种姜，对生姜的产量和品质都有影响。|防治方法|姜窖内防治，在生姜入窖前要彻底清扫，用90%敌百虫800倍液喷窖。在田间要注意精选姜种，淘汰被害种姜，播前用90%敌百虫800倍液浸泡种姜5-10min可以杀死种姜内的害虫。', '2017-09-20', 'picture\\wrom\\shucai\\6.jpg'),
(7, 1, '西葫芦黄曲条跳甲的防治方法', '黄曲条跳甲属鞘翅目、叶甲科害虫，俗称狗虱虫、跳虱，简称跳甲，常为害叶菜类蔬菜，以甘蓝、花椰菜、白菜、菜薹、萝卜、芜菁、油菜等十字花科蔬菜为主，但也为害茄果类、瓜类、豆类蔬菜|虫态有成虫、卵、幼虫、蛹，以成虫和幼虫两个虫态对植株直接造成危害。成虫食叶，以幼苗期最重。在留种地主要为害花蕾和嫩荚。幼虫只害菜根，蛀食根皮，咬断须根，使叶片萎蔫枯死。萝卜被害呈许多黑斑，最后整个变黑腐烂。白菜受害叶片变黑死亡，并传播软腐病。|黄曲条跳甲成虫和幼虫均可为害，成虫咬食西葫芦叶片造成许多小孔。幼虫可为害西葫芦根部，影响植株正常生长。黄曲条跳甲成虫在残株、杂草、土缝等处越冬，春季气温回升到10℃左右时开始活动为害。|防治方法：发现幼虫为害时，可用90%敌百虫或50%敌敌畏乳油1000倍液灌根。|防治成虫，一般从西葫芦地的四周开始喷药，逐渐向中央围喷，防止成虫逃走。可用90%敌百虫1000倍液，50%敌敌畏乳油1000倍液，50%辛硫磷乳油1000倍液，20%灭杀毙4000倍液。|清除田间残株、落叶和杂草，深翻晒土，都可减轻为害。', '2017-09-20', 'picture\\wrom\\shucai\\7.jpg'),
(8, 1, '黄瓜常见虫害的防治', '黄瓜在进行种植的过程中经常会受到虫害的威胁，那么我们应该怎样才能够对于这些虫害进行防治呢，下面我们就针对于黄瓜常见的白粉虱和美洲斑潜蝇来为大家讲解他们的防治方法。|又名温室白粉虱、白飞虱，其群居于番茄植株的叶背吸食汁液，被害叶片褪绿、变黄、萎蔫，并分泌蜜露诱发煤污病，并传播某些植物病毒，加重病毒病的发生。|防治措施：通风口处设置防虫网。于白粉虱发生初期，可采取黄板诱杀的办法。可用灭蚜宁烟雾剂(每亩330克)、或蚜虱一熏净烟剂(每亩300～400克)进行熏杀，每7～8天熏1次，连熏2～3次。可喷洒10%吡虫威乳油400～600倍液或25%扑虱灵1500倍液等，先熏后喷效果更好。|美洲斑潜蝇|防治措施：药剂防治：|应“早”和“准”，重点抓好苗期防治。掌握在幼虫2龄前(虫道很小时)喷药，喷药时间最好在上午8～12时。可用98%巴丹原粉1500倍液、1.8%爱福丁乳油3000倍液、25%杀虫双水剂500倍液、40%绿菜宝乳油1000倍液、0.12%天力Ⅱ号可湿性粉剂4000倍液、20%好年冬乳油1500～2000倍液、48%乐斯本乳油800～1000倍液等。', '2017-09-20', 'picture\\wrom\\shucai\\8.jpg');

